html,body
{
    width: 100%;
    height: 100%;
    margin: 0px;
    padding: 0px;
    overflow-x: hidden; 
    font-family: Arial;
}


body, h1, h2,h3 a, ul3 li {
    margin: 0;
    font-family: Arial;
    padding: 0;
    width: 100%;
}

.banner {
    position: relative;
    text-align: center;

}

.banner img {
    width: 100%;
    height: 400px;
}

header {
    max-width: 100%;
    position: fixed;
    text-align: center;
    background-color: #0c1ca8;
    padding: 10px;
    width: 100%;
    z-index: 1;
    top: 0;
    left: 0;
}

header h1 {
    max-width: 70%;
    color: white;
    border: 2px solid #5968ec;
    padding: 6px 12px;
    display: inline-block;
    border-radius: 10px;
    margin: 0 auto;
}

.banner .welcome {
    position: absolute;
    top: 25%;
    left: 0px;
    background-color: #0c1ca8;
    color: white;
    padding: 5px;
    max-width: 400px;
}

.welcome h2 {
    font-size: 24px;
}

.welcome h2 span {
    font-size: 1.2em;
}

nav {
    max-width: 100%;
    background-color: #f4f4f4;
    padding: 15px 20%;
    position: sticky;
    top: 70px;
    z-index: 5;
}

nav ul {
    white-space: nowrap;
    max-width: 100%;
    margin: 0 auto;
}

nav li {
    min-width: 48px;
    min-height: 48px;

    display: inline-block;
}

nav li a {
    width: 100%;
    text-decoration: none;
    font-size:16px;
    background-color: #0c1ca8;
    padding: 10px;
    border-radius: 5px;
    color:white;
}

nav li a.join {
    color: #f0db1c;
}

@media screen and (max-width: 1400px) {
    .banner .welcome h2 {
        font-size: 15px;
    }
    .banner .welcome {
        top: 35%;
    }
    li {
        font-size: 10px;
    }
}



@media screen and (max-width: 550px) {
    header h1 {
        font-size: 1em;
    }

    .mainContent p {
        line-height:150%
    }

    .banner .welcome br {
        display: none;

    }

    .banner img {
        height: 200px;
    }



    nav {
        top: 110px;
        padding: 15px 0 0 0;
        position: static;
        z-index: 5;
    }


    nav ul {
        white-space: nowrap;
        max-width: 100%;
        margin: 0;
    }

    nav li {
        min-width: 48px;
        min-height: 48px;

        display: block;
    }

    nav li a {
        width: 100%;
        text-decoration: none;
        font-size:16px;
        background-color: #0c1ca8;
        padding: 10px;
        border-radius: 5px;
        color:white;
    }


    .images li{
        display: block;
        width: 100%;
        margin: 25px auto;
    }

    .mainContent {
        width: 90%;
        margin: 5px 5px;
        padding: 0 20px;
    }
}


main {
    max-width: 100%;
    width: 1200px;
    margin: 80px auto;
    padding: 0 40px;
    box-sizing: border-box;
}

article h2 {
    color: #f63232;
    font-size: 32px;
}

article p {
    line-height: 2em;
    color: #161616;
}

.images {
    text-align: center;
    margin: 80px 0;
    /* white-space: nowrap; */
}




iframe:focus {
    outline: none;
  }

#external_link{
    text-decoration: none;
    padding: 10px;
    border-radius: 5px;
}

xmp{
    border: 3px solid #0c1ca8;
    padding-left: 30px;;
}

section.join {
    background-color: #f4f4f4;
    text-align: center;
    padding: 60px 20px;
    color: #4b4b4b;
}

.join h2 {
    font-size: 36px;
}

form input {
    margin: 20px 0;
    padding: 10px 20px;
    font-size: 24px;
    border-radius: 28px;
    border: 4px solid white;
}

footer {
    background-color: #0c1ca8;
    color: white;
    padding: 10px;
    text-align: center;
}

nav li a:hover {
    text-decoration: underline;
}

.images li img:hover {
    position: relative;
    top: -10px;
    left: -10px;
}

.join form input:focus {
    border: 4px dashed #4b4b4b;
    outline: none;
}

form input:valid {
    border: 4px solid #71d300;
}

form input:invalid {
    border: 4px solid #f63232;
}

article p::first-line {
    font-size: 1.2em;
    font-weight: bold;
}

section.join p::first-letter {
    /* font-size: 1.5em; */
    /* font-weight: bold; */
    font-family: Georgia, 'Times New Roman', Times, serif;
}

p::selection {
    background-color: #f63232;
    color: white;
}

* {
    user-select: none
}

.container {
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100vh;
}

#board {
    width: 600px;
    height: 600px;
    margin: 0 auto;
    background-color: #231f20;
    color: #62c559;
    border: 6px solid #62c559;
    border-radius: 10px;
    display: grid;
    grid-template: repeat(3, 1fr) / repeat(3, 1fr);
}

.box {
    border: 6px solid #62c559;
    font-family: Helvetica;
    font-weight: bold;
    font-size: 4em;
    display: flex;
    justify-content: center;
    align-items: center;
    cursor: pointer;
}

.row {
    display: flex;
    justify-content: space-between;
}

.button {
    background-color: #62c559;
    border: none;
    border-radius: 6px;
    color: white;
    padding: 16px 32px;
    text-align: center;
    text-decoration: none;
    text-transform: uppercase;
    font-size: 16px;
    margin-top: 16px;
    margin-bottom: 16px;
    cursor: pointer;
    width: 200px;
}

.button:active, .button:focus {
    outline: 0;
    border: none;
    -moz-outline-style: none;
}

.button:disabled {
    cursor: not-allowed;
    background-color: #e7e7e7;
}

.button-hoverable span {
    cursor: pointer;
    display: inline-block;
    position: relative;
    transition: 0.5s;
}

.button-hoverable span:after {
    content: '\00bb';
    position: absolute;
    opacity: 0;
    top: 0;
    right: -20px;
    transition: 0.5s;
}

.button-hoverable:hover span {
    padding-right: 25px;
}

.button-hoverable:hover span:after {
    opacity: 1;
    right: 0;
}


